mapper.select:
SELECT

,
count(a.`APPLICATION_ID`) as interfaceCount
FROM `app_source_inter` a
WHERE 1 = 1

    AND a.`ALIVE_FLAG` = #{aliveFlag}


    AND a.`ADD_USER_ID` = #{addUserId}


    AND a.`APPLICATION_ID` = #{applicationId}


    AND a.`DATA_SOURCE_ID` = #{dataSourceId}

GROUP BY a.`APPLICATION_ID`
ORDER BY a.`ADD_TIME` DESC

SELECT , p.ROUTER_PATH as routerPath, i.`INTERFACE_NAME` as interfaceName, t.`TYPE_NAME` as typeName, l.`LABEL_NAME` as labelName, i.`URL` as url FROM `app_source_inter` a LEFT JOIN application_info p ON a.`APPLICATION_ID` = p.`APPLICATION_ID` LEFT JOIN interface_info i ON a.`DATA_SOURCE_ID` = i.`DATA_SOURCE_ID` AND a.`INTERFACE_ID` = i.`INTERFACE_ID` LEFT JOIN interface_type_info t ON i.`TYPE_ID` = t.`TYPE_ID` LEFT JOIN interface_label_info l ON i.`LABEL_ID` = l.`LABEL_ID` WHERE 1 = 1
AND a.`ALIVE_FLAG` = #{aliveFlag}
AND a.`ADD_USER_ID` = #{addUserId}
AND a.`APPLICATION_ID` = #{applicationId}
AND a.`DATA_SOURCE_ID` = #{dataSourceId}
AND i.`INTERFACE_NAME` LIKE CONCAT('%',#{interfaceName},'%')
AND t.`TYPE_NAME` LIKE CONCAT('%',#{typeName},'%')
AND l.`LABEL_NAME` LIKE CONCAT('%',#{labelName},'%')
ORDER BY a.`ADD_TIME` DESC
