mapper.select:
SELECT

,
d.`DATA_SOURCE_NAME` as dataSourceName,
count(a.`APPLICATION_ID`) as interfaceCount
FROM `app_source_inter` a
LEFT JOIN data_source_info d ON a.`DATA_SOURCE_ID` = d.`DATA_SOURCE_ID`
WHERE 1 = 1

    AND a.`ALIVE_FLAG` = #{aliveFlag}


    AND a.`ADD_USER_ID` = #{addUserId}


    AND a.`APPLICATION_ID` = #{applicationId}


    AND a.`DATA_SOURCE_ID` = #{dataSourceId}


    AND d.`DATA_SOURCE_NAME` = #{dataSourceName}

GROUP BY a.`DATA_SOURCE_ID`
ORDER BY a.`ADD_TIME` DESC

SELECT , d.`DATA_SOURCE_NAME` as dataSourceName, p.ROUTER_PATH as routerPath, i.`INTERFACE_NAME` as interfaceName, t.`TYPE_NAME` as typeName, l.`LABEL_NAME` as labelName, i.`URL` as url FROM `app_source_inter` a LEFT JOIN application_info p ON a.`APPLICATION_ID` = p.`APPLICATION_ID` LEFT JOIN data_source_info d ON a.`DATA_SOURCE_ID` = d.`DATA_SOURCE_ID` LEFT JOIN interface_info i ON a.`DATA_SOURCE_ID` = i.`DATA_SOURCE_ID` AND a.`INTERFACE_ID` = i.`INTERFACE_ID` LEFT JOIN interface_type_info t ON i.`TYPE_ID` = t.`TYPE_ID` LEFT JOIN interface_label_info l ON i.`LABEL_ID` = l.`LABEL_ID` WHERE 1 = 1
AND a.`ALIVE_FLAG` = #{aliveFlag}
AND a.`ADD_USER_ID` = #{addUserId}
AND a.`APPLICATION_ID` = #{applicationId}
AND a.`DATA_SOURCE_ID` = #{dataSourceId}
AND i.`INTERFACE_NAME` LIKE CONCAT('%',#{interfaceName},'%')
AND t.`TYPE_NAME` LIKE CONCAT('%',#{typeName},'%')
AND l.`LABEL_NAME` LIKE CONCAT('%',#{labelName},'%')
ORDER BY a.`ADD_TIME` DESC

select d.`DATA_SOURCE_ID` as dataSourceId, d.`DATA_SOURCE_NAME` as dataSourceName, d.`DATA_SOURCE_CODE` as dataSourceCode, d.`ALIVE_FLAG` as aliveFlag, d.`ADD_USER_ID` as addUserId, d.`ADD_TIME` as addTime, d.`OPR_TIME` as oprTime, d.`OPR_USER_ID` as oprUserId from data_source_info d where not exists (select * from app_source_inter a where a.`DATA_SOURCE_ID` = d.`DATA_SOURCE_ID` and a.`APPLICATION_ID` = #{applicationId})
AND d.`ADD_USER_ID` = #{addUserId}
AND d.`ALIVE_FLAG` = #{aliveFlag}
AND d`DATA_SOURCE_NAME` LIKE CONCAT('%',#{dataSourceName},'%')
