mapper.select:
SELECT u.`ACCOUNT` AS account, u.`USER_ID` AS userId, u.`P_USER_ID` AS parentUserId, u.`PASSWORD` AS `password`, u.`USER_NAME` AS userName, u.`USE_NAME` AS useName, u.`PHONE` AS phone, u.`EMAIL` AS email, u.`ALIVE_FLAG` AS aliveFlag, u.`ADD_USER_ID` AS addUserId, u.`ADD_TIME` AS `addTime`, u.`OPR_USER_ID` AS oprUserId, u.`OPR_TIME` AS oprTime FROM user_info u LEFT JOIN role_user r ON u.`USER_ID` = r.`USER_ID` WHERE u.`ALIVE_FLAG` = #{aliveFlag}
and u.`P_USER_ID` = #{parentUserId}
and r.`ROLE_ID` = #{roleId}
and u.`USER_NAME` LIKE CONCAT('%',#{userName},'%')
and u.`USE_NAME` LIKE CONCAT('%',#{useName},'%')
and u.`ACCOUNT` LIKE CONCAT('%',#{account},'%')
